import React, { useState } from 'react';
import './App.scss';

function App() {
  const [quote, setQuote] = useState("Life isn’t about getting and having, it’s about giving and being.")
  const [author, setAuthor] = useState("Kevin Kruse")

  return (
    <div className="App">
      <header className="App-header">
        <p>
          "{quote}"
        </p>
        <p>
          - {author}
        </p>
        <button onClick={() => setQuote("We become what we think about.")}>Change Quote</button>
      </header>
    </div>
  );
}

export default App;
