import React, { useState } from 'react';
import './App.scss';

function App() {
  const [quote, setQuote] = useState("Life isn’t about getting and having, it’s about giving and being.")
  const [author, setAuthor] = useState("Kevin Kruse")
  const [randomNumber, setRandomNumber] = useState(0)



  const changeQuoteAndAuthor = () => {
    setQuote("We become what we think about.");
    setAuthor("Earl Nightingale")
  }

  const generateRandomNumber = () => {
    setRandomNumber(3 * Math.random())
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Random Number: {randomNumber}</h1>
        <button onClick={() => generateRandomNumber()}>generate random number</button>
        <p>
          "{quote}"
        </p>
        <p>
          - {author}
        </p>
        <button onClick={() => changeQuoteAndAuthor()}>Change Quote</button>
      </header>
    </div>
  );
}

export default App;
